import { useEffect, useState } from "react";

const ToDo = () => {
  const [data, setData] = useState<any>();
  const [value,setValue] = useState("");
  const[token,setToken] = useState("")

  const fetchData = async() => {

    const response =await fetch("http://localhost:8082/getAll",{
      method:'GET',
      headers:{
        token:'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjY4ODdjOGQ5NTI5MzgxYTc5NWY3ZTA0ZSIsImlhdCI6MTc1MzkwMTIyNX0.QNuwMiigsM23CBj9D5InU7qDuY1Sa3wr_xbPiujQRNA'
      }
    })
    const apiData = await response.json()
    setData(apiData?.todos)
    console.log("qwerty",apiData);
  }

  const deleteToDo = async(id:String) => {
      await fetch("http://localhost:8082/delete",{
      method:'POST',
      body:JSON.stringify({id}),
      headers:{
        token:'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjY4ODdjOGQ5NTI5MzgxYTc5NWY3ZTA0ZSIsImlhdCI6MTc1MzkwMTIyNX0.QNuwMiigsM23CBj9D5InU7qDuY1Sa3wr_xbPiujQRNA',
        'Content-Type':'application/json'
      }
    })
    .finally(() => {
      fetchData();
    })
  }

  const createToDo = async(title:String)=>{
    await fetch("http://localhost:8082/createToDo",{
      method:'POST',
      body:JSON.stringify({title}),
      headers:{
        token:'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjY4ODdjOGQ5NTI5MzgxYTc5NWY3ZTA0ZSIsImlhdCI6MTc1MzkwMTIyNX0.QNuwMiigsM23CBj9D5InU7qDuY1Sa3wr_xbPiujQRNA',
        'Content-Type':'application/json'
      }
      })
      .finally(() => {
        fetchData();
      }
    )

  }

  useEffect(() => {
    fetchData();
  },[])


  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        backgroundColor: "#181f38",
        width: "100%",
        height: "100vh",
      }}
    >
      <div
        style={{
          border: "2px solid",
          width: "600px",
          height: "400px",
          borderRadius: "20px",
          backgroundColor: "#25283c",
        }}
      >
        <div style={{ margin: "30px" }}>
          <h1 style={{ color: "#7b49b5" }}>TODO</h1>
          <input
            type="text"
            placeholder="Enter a todo..."
            style={{
              width: "-webkit-fill-available",
              height: "35px",
              border: "none",
              borderRadius: "10px",
              backgroundColor: "#3b3f51",
              color: "white",
              textIndent: "10px",
            }}
            onChange={(e) => setValue(e.target.value)}
            value={value}
          />

          <button
            style={{
              width: "-webkit-fill-available",
              height: "35px",
              border: "none",
              borderRadius: "10px",
              backgroundColor: "#531d94",
              marginTop: "20px",
              color: "white",
            }}
            onClick={() => createToDo(value)}
          >
            Add Todo
          </button>

      { data && data?.length > 0 && data.map((item:any) => (
        <>

        <div
            style={{
              width: "-webkit-fill-available",
              height: "40px",
              border: "none",
              borderRadius: "10px",
              backgroundColor: "#3b3f51",
              display: "flex",
              marginTop: "20px",
              justifyContent: "space-between",
              alignItems: "center",
            }}
          >
            <div style={{ margin: "10px", color: "white" }}>{item?.title}</div>
            <button
              style={{
                margin: "10px",
                color: "white",
                backgroundColor: "#b82f4c",
                borderRadius: "10px",
                border: "none",
                padding: "6px 13px 6px 13px",
              }}
              onClick={() => deleteToDo(item?._id)}
            >
              Delete
            </button>
          </div>
          </>))}

          <div style={{ marginTop: "20px" }}>
            <span style={{ color: "yellow", cursor: "pointer" }}>Sign Out</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ToDo;
