import React, { useState } from "react";

const SignUpPage = () => {
    const [username,setUsername] = useState("");
    const [password,setPassword] = useState("");

  const fetchData = async() => {

    const response =await fetch("http://localhost:8082/signup",{
      method:'POST',
      body:JSON.stringify({
        username:username,
        password:password
      }),
      headers:{
        'Content-Type':'application/json'
        }
    })
  }

  const handleSubmit = ()=>{
    fetchData();
  }

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        backgroundColor: "#181f38",
        width: "100%",
        height: "100vh",
      }}
    >
      <div
        style={{
          border: "2px solid",
          width: "600px",
          height: "400px",
          borderRadius: "20px",
          backgroundColor: "#25283c",
        }}
      >
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "flex-start",
            margin: "30px",
          }}
        >
          <h1 style={{ color: "#7b49b5" }}>Signup</h1>
          <label style={{ color: "#7b49b5", marginBottom: "5px" }}>
            Username:
          </label>
          <input
            type="text"
            aria-label="Username"
            style={{
              width: "-webkit-fill-available",
              height: "35px",
              border: "none",
              borderRadius: "10px",
              backgroundColor: "#3b3f51",
              color: "white",
            }}
            onChange={(e) => setUsername(e.target.value)}
            value={username}

          />

          <label
            style={{ color: "#7b49b5", marginBottom: "5px", marginTop: "10px" }}
          >
            Password:
          </label>
          <input
            type="text"
            style={{
              width: "-webkit-fill-available",
              height: "35px",
              border: "none",
              borderRadius: "10px",
              backgroundColor: "#3b3f51",
              color: "white",
            }}
            onChange={(e) => setPassword(e.target.value)}
            value={password}
          />

          <button
            style={{
              width: "-webkit-fill-available",
              height: "35px",
              border: "none",
              borderRadius: "10px",
              backgroundColor: "#531d94",
              marginTop: "20px",
              color: "white",
            }}
            onClick={() => handleSubmit()}
          >
            Signup
          </button>
          <div style={{ marginTop: "20px" }}>
            <span style={{ color: "#7b49b5" }}>Already have an account? </span>
            <span style={{ color: "yellow", cursor: "pointer" }}>Sign in</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SignUpPage;
